where id = #{id}

update `act_activity_apply`
activity_apply_status = #{activityApply.activityApplyStatus,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler,javaType=com.zy.entity.act.ActivityApply$ActivityApplyStatus}, user_id = #{activityApply.userId}, payer_user_id = #{activityApply.payerUserId}, amount = #{activityApply.amount}, activity_id = #{activityApply.activityId}, applied_time = #{activityApply.appliedTime}, is_cancelled = #{activityApply.isCancelled}, inviter_id = #{activityApply.inviterId}, is_sms_sent = #{activityApply.isSmsSent}, outer_sn = #{activityApply.outerSn},
