mapper.update:
update `fnc_bank_card`
user_id = #{userId}, realname = #{realname}, card_number = #{cardNumber}, bank_id = #{bankId}, bank_name = #{bankName}, bank_branch_name = #{bankBranchName}, is_default = #{isDefault}, is_deleted = #{isDeleted}, confirm_status = #{confirmStatus,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, confirm_remark = #{confirmRemark}, confirmed_time = #{confirmedTime}, applied_time = #{appliedTime},
where id = #{id}

update `fnc_bank_card`
user_id = #{bankCard.userId}, realname = #{bankCard.realname}, card_number = #{bankCard.cardNumber}, bank_id = #{bankCard.bankId}, bank_name = #{bankCard.bankName}, bank_branch_name = #{bankCard.bankBranchName}, is_default = #{bankCard.isDefault}, is_deleted = #{bankCard.isDeleted}, confirm_status = #{bankCard.confirmStatus,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler,javaType=com.zy.entity.sys.ConfirmStatus}, confirm_remark = #{bankCard.confirmRemark}, confirmed_time = #{bankCard.confirmedTime}, applied_time = #{bankCard.appliedTime},
where id = #{bankCard.id}

mapper.select:
select
id,user_id,realname,card_number,bank_id,bank_name,bank_branch_name,is_default,is_deleted,confirm_status,confirm_remark,confirmed_time,applied_time
from `fnc_bank_card`


		and is_deleted = #{isDeletedEQ}


		and user_id = #{userIdEQ}


		and confirm_status = #{confirmStatusEQ,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


		 and realname like #{realnameLK}




		order by ${orderByAndDirection}


		order by id desc



	limit #{offset}, #{pageSize}

select count(*) from `fnc_bank_card`


		and is_deleted = #{isDeletedEQ}


		and user_id = #{userIdEQ}


		and confirm_status = #{confirmStatusEQ,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


		 and realname like #{realnameLK}
