mapper.delete:
update `cms_banner`
SET update_id = #{updateId},
	update_time = #{updateTime},
	status = #{status}
where id = #{id}

mapper.update:
update `cms_banner`
is_released = #{isReleased}, image = #{image}, url = #{url}, title = #{title}, order_number = #{orderNumber}, banner_position = #{bannerPosition,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, is_open_blank = #{isOpenBlank},
where id = #{id}

update `cms_banner`
is_released = #{banner.isReleased}, image = #{banner.image}, url = #{banner.url}, title = #{banner.title}, order_number = #{banner.orderNumber}, banner_position = #{banner.bannerPosition,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler,javaType=com.zy.entity.cms.Banner$BannerPosition}, is_open_blank = #{banner.isOpenBlank}, update_id = #{banner.updateId}, update_time = #{banner.updateTime},
where id = #{banner.id}

mapper.select:
select

from `cms_banner`

	status = 1

		 and title like #{titleLK}


		and banner_position = #{bannerPositionEQ,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


		and is_released = #{isReleasedEQ}




		order by ${orderByAndDirection}


		order by id desc



	limit #{offset}, #{pageSize}

select count(*) from `cms_banner`


		 and title like #{titleLK}


		and banner_position = #{bannerPositionEQ,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


		and is_released = #{isReleasedEQ}
