mapper.update:
update `fnc_deposit`
user_id = #{userId}, title = #{title}, sn = #{sn}, currency_type1 = #{currencyType1,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, amount1 = #{amount1}, currency_type2 = #{currencyType2,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, amount2 = #{amount2}, total_amount = #{totalAmount}, is_outer_created = #{isOuterCreated}, outer_sn = #{outerSn}, qr_code_url = #{qrCodeUrl}, weixin_open_id = #{weixinOpenId}, pay_type = #{payType,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, paid_time = #{paidTime}, created_time = #{createdTime}, expired_time = #{expiredTime}, deposit_status = #{depositStatus,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, remark = #{remark}, offline_image = #{offlineImage}, offline_memo = #{offlineMemo}, operator_id = #{operatorId}, version = version + 1,
where id = #{id} and version=#{version}

update `fnc_deposit`
user_id = #{deposit.userId}, title = #{deposit.title}, sn = #{deposit.sn}, currency_type1 = #{deposit.currencyType1,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler,javaType=com.zy.entity.fnc.CurrencyType}, amount1 = #{deposit.amount1}, currency_type2 = #{deposit.currencyType2,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler,javaType=com.zy.entity.fnc.CurrencyType}, amount2 = #{deposit.amount2}, total_amount = #{deposit.totalAmount}, is_outer_created = #{deposit.isOuterCreated}, outer_sn = #{deposit.outerSn}, qr_code_url = #{deposit.qrCodeUrl}, weixin_open_id = #{deposit.weixinOpenId}, pay_type = #{deposit.payType,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler,javaType=com.zy.entity.fnc.PayType}, paid_time = #{deposit.paidTime}, created_time = #{deposit.createdTime}, expired_time = #{deposit.expiredTime}, deposit_status = #{deposit.depositStatus,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler,javaType=com.zy.entity.fnc.Deposit$DepositStatus}, remark = #{deposit.remark}, offline_image = #{deposit.offlineImage}, offline_memo = #{deposit.offlineMemo}, operator_id = #{deposit.operatorId},
where id = #{deposit.id}

mapper.select:
select
id,user_id,title,sn,currency_type1,amount1,currency_type2,amount2,total_amount,is_outer_created,outer_sn,qr_code_url,weixin_open_id,pay_type,paid_time,created_time,expired_time,deposit_status,remark,offline_image,offline_memo,operator_id,version
from `fnc_deposit`


		and sn = #{snEQ}


		and created_time < #{createdTimeLT}


		
		and user_id in
		
		#{item}
		
		
		
		1 = 2



		
		and deposit_status in
		
		#{item,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}
		
		
		
		1 = 2



		and pay_type = #{payTypeEQ,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


		and user_id = #{userIdEQ}


		and paid_time < #{paidTimeLT}


		and created_time >= #{createdTimeGTE}


		and deposit_status = #{depositStatusEQ,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


		and paid_time >= #{paidTimeGTE}


		and expired_time < #{expiredTimeLT}




		order by ${orderByAndDirection}


		order by id desc



	limit #{offset}, #{pageSize}

select count(*) from `fnc_deposit`


		and sn = #{snEQ}


		and created_time < #{createdTimeLT}


		
