mapper.update:
update `sys_message`
title = #{title}, content = #{content}, user_id = #{userId}, is_read = #{isRead}, created_time = #{createdTime}, read_time = #{readTime}, message_type = #{messageType,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, batch_number = #{batchNumber}, token = #{token},
where id = #{id}

update `sys_message`
title = #{message.title}, content = #{message.content}, user_id = #{message.userId}, is_read = #{message.isRead}, created_time = #{message.createdTime}, read_time = #{message.readTime}, message_type = #{message.messageType,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler,javaType=com.zy.entity.sys.Message$MessageType}, batch_number = #{message.batchNumber}, token = #{message.token},
where id = #{message.id}

mapper.select:
select
id,title,content,user_id,is_read,created_time,read_time,message_type,batch_number,token
from `sys_message`


		 and title like #{titleLK}


		 and content like #{contentLK}


		 and message_type = #{messageTypeEQ,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


		 and batch_number = #{batchNumberEQ}


		 and user_id = #{userIdEQ}


		 and is_read = #{isReadEQ}




		order by ${orderByAndDirection}


		order by id desc



	limit #{offset}, #{pageSize}

select count(*) from `sys_message`


		 and title like #{titleLK}


		 and content like #{contentLK}


		 and message_type = #{messageTypeEQ,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


		 and batch_number = #{batchNumberEQ}


		 and user_id = #{userIdEQ}


		 and is_read = #{isReadEQ}
