mapper.update:
update `td_order_store`
user_id = #{userId}, order_id = #{orderId}, type = #{type}, number = #{number}, before_number = #{beforeNumber}, after_number = #{afterNumber}, create_date = #{createDate}, create_by = #{createBy}, is_end = #{isEnd},
where id = #{id}

update `td_order_store`
user_id = #{orderStore.userId}, order_id = #{orderStore.orderId}, type = #{orderStore.type}, number = #{orderStore.number}, before_number = #{orderStore.beforeNumber}, after_number = #{orderStore.afterNumber}, create_date = #{orderStore.createDate}, create_by = #{orderStore.createBy}, is_end = #{orderStore.isEnd},
where id = #{orderStore.id}

mapper.select:
SELECT

FROM td_order_store a LEFT JOIN usr_user b ON a.user_id = b.id
	LEFT JOIN usr_user_info c ON a.user_id = c.user_id


		AND c.realname = #{realName}


		AND b.phone = #{phone}


		and a.create_date >= #{createDateGTE}


		and a.before_number >= #{beforeNumberGTE}


		and a.after_number >= #{afterNumberGTE}


		and a.after_number < #{afterNumberLT}


		and a.type = #{typeEQ}


		and a.user_id = #{userIdEQ}


		and a.create_by = #{createByEQ}


		and a.create_date <= #{createDateLT}


		and a.order_id = #{orderIdEQ}


		and a.is_end = #{isEndEQ}


		and a.before_number < #{beforeNumberLT}


		and a.number >= #{numberGTE}


		and a.number < #{numberLT}




		order by ${orderByAndDirection}


		order by id desc



	limit #{offset}, #{pageSize}

SELECT count(*)
	FROM td_order_store a LEFT JOIN usr_user b ON a.user_id = b.id
LEFT JOIN usr_user_info c ON a.user_id = c.user_id


		AND c.realname = #{realName}


		AND b.phone = #{phone}


		and a.create_date >= #{createDateGTE}


		and a.before_number >= #{beforeNumberGTE}


		and a.after_number >= #{afterNumberGTE}


		and a.after_number < #{afterNumberLT}


		and a.type = #{typeEQ}


		and a.user_id = #{userIdEQ}


		and a.create_by = #{createByEQ}


		and a.create_date <= #{createDateLT}


		and a.order_id = #{orderIdEQ}


		and a.is_end = #{isEndEQ}


		and a.before_number < #{beforeNumberLT}


		and a.number >= #{numberGTE}


		and a.number < #{numberLT}
