mapper.update:
update `act_report`
user_id = #{userId}, realname = #{realname}, age = #{age}, gender = #{gender,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, phone = #{phone}, job_id = #{jobId}, area_id = #{areaId}, tag_ids = #{tagIds}, report_result = #{reportResult,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, text = #{text}, image = #{image}, times = #{times}, reported_date = #{reportedDate}, applied_time = #{appliedTime}, created_time = #{createdTime}, pre_confirm_status = #{preConfirmStatus,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, pre_confirmed_time = #{preConfirmedTime}, confirm_status = #{confirmStatus,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, confirm_remark = #{confirmRemark}, confirmed_time = #{confirmedTime}, is_settled_up = #{isSettledUp}, is_hot = #{isHot}, version = version + 1,
where id = #{id} and version=#{version}

