mapper.select:
select
id,user_id,realname,age,gender,phone,job_id,area_id,tag_ids,report_result,text,image1,image2,image3,image4,image5,image6,applied_time,created_time,pre_confirm_status,confirm_status,confirm_remark,confirmed_time,is_settled_up,is_hot,version
from `act_report`


		and pre_confirm_status = #{preConfirmStatusEQ,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


		
		and user_id in
		
		#{item}
		
		
		
		1 = 2



		and is_hot = #{isHotEQ}


		and user_id = #{userIdEQ}


		and phone = #{phoneEQ}


