mapper.update:
update `rpt_salesvolume`
user_rank = #{userRank}, user_name = #{userName}, user_phone = #{userPhone}, amount_reached = #{amountReached}, amount_target = #{amountTarget}, achievement = #{achievement}, ranking = #{ranking}, number = #{number}, type = #{type}, area_type = #{areaType}, create_time = #{create_time},
where id = #{id}

update `rpt_salesvolume`
user_rank = #{salesVolume.userRank}, user_name = #{salesVolume.userName}, user_phone = #{salesVolume.userPhone}, amount_reached = #{salesVolume.amountReached}, amount_target = #{salesVolume.amountTarget}, achievement = #{salesVolume.achievement}, ranking = #{salesVolume.ranking}, number = #{salesVolume.number}, type = #{salesVolume.type}, area_type = #{salesVolume.areaType}, create_time = #{salesVolume.createTime},
where id = #{salesVolume.id}

mapper.select:
select

from `rpt_salesvolume`


		and user_rank = #{userRankEQ}


		and user_phone = #{userPhoneLK}


		and id = #{idEQ}


		and is_boss = #{isBoss}


		and area_type = #{areaTypeEQ}


		and DATE_FORMAT(create_time, '%Y-%m') = DATE_FORMAT(#{createTime}, '%Y-%m')


		and user_name = #{userNameLK}




		order by ${orderByAndDirection}


		order by user_rank desc



	limit #{offset}, #{pageSize}

select count(*) from `rpt_salesvolume`


		and user_rank = #{userRankEQ}


		and user_phone = #{userPhoneLK}


		and id = #{idEQ}


		and is_boss = #{isBoss}


		and area_type = #{areaTypeEQ}


		and DATE_FORMAT(create_time, '%Y-%m') = DATE_FORMAT(#{createTime}, '%Y-%m')


		and user_name = #{userNameLK}
