mapper.update:
update `ts_team_report`
user_id = #{userId}, user_name = #{userName}, district_name = #{districtName}, district_id = #{districtId}, extra_number = #{extraNumber}, newextra_number = #{newextraNumber}, newextra_rate = #{newextraRate}, province_number = #{provinceNumber}, newprovince_number = #{newprovinceNumber}, newprovince_rate = #{newprovinceRate}, ranking = #{ranking}, year = #{year}, month = #{month}, phone = #{phone}, create_date = #{createDate},
where id = #{id}

update `ts_team_report`
user_id = #{teamReportNew.userId}, user_name = #{teamReportNew.userName}, district_name = #{teamReportNew.districtName}, district_id = #{teamReportNew.districtId}, extra_number = #{teamReportNew.extraNumber}, newextra_number = #{teamReportNew.newextraNumber}, newextra_rate = #{teamReportNew.newextraRate}, province_number = #{teamReportNew.provinceNumber}, newprovince_number = #{teamReportNew.newprovinceNumber}, newprovinceRate = #{teamReportNew.newprovinceRate}, ranking = #{teamReportNew.ranking}, year = #{teamReportNew.year}, month = #{teamReportNew.month}, phone = #{teamReportNew.phone}, create_date = #{teamReportNew.createDate},
where id = #{teamReportNew.id}

mapper.select:
select

from `ts_team_report`


		and extra_number = #{extraNumberEQ}


		and province_number = #{provinceNumberEQ}


		and newextra_rate = #{newextraRateEQ}


		and newprovince_number = #{newprovinceNumberEQ}


		 and district_name like #{districtNameLK}


		and ranking = #{rankingEQ}


		and user_id = #{userIdEQ}


		and district_id = #{districtIdEQ}


		and create_date = #{createDateEQ}


		and month = #{monthEQ}


		 and user_name like #{userNameLK}


		and phone = #{phoneEQ}


		and newextra_number = #{newextraNumberEQ}


		and newprovince_rate = #{newprovinceRateEQ}


		and year = #{yearEQ}




		order by ${orderByAndDirection}


		order by id desc



	limit #{offset}, #{pageSize}

select count(*) from `ts_team_report`


		and extra_number = #{extraNumberEQ}


		and province_number = #{provinceNumberEQ}


		and newextra_rate = #{newextraRateEQ}


		and newprovince_number = #{newprovinceNumberEQ}


