mapper.select:
select

from `usr_user_info`

	
		
		and user_id in
		
		#{item}
		
		
		
		and 1 = 2
		
	
	
		 and id_card_number like #{idCardNumberLK}
	
	
		
		and area_id in
		
		#{item}
		
		
		
		and 1 = 2
		
	
	
		and user_id = #{userIdEQ}
	
	
		and confirm_status = #{confirmStatusEQ,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}
	
	
		 and realname like #{realnameLK}
	
	
		and area_id = #{areaIdEQ}
	
	
		and real_flag = #{realFlag}
	


	
		order by ${orderByAndDirection}
	
	
		order by id desc
	


	limit #{offset}, #{pageSize}

select count(*) from `usr_user_info`

	
		
		and user_id in
		
		#{item}
		
		
		
		and 1 = 2
		
	
	
		 and id_card_number like #{idCardNumberLK}
	
	
		
		and area_id in
		
		#{item}
		
		
		
		and 1 = 2
		
	
	
		and user_id = #{userIdEQ}
	
	
		and confirm_status = #{confirmStatusEQ,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}
	
	
		 and realname like #{realnameLK}
	
	
		and area_id = #{areaIdEQ}
	
	
		and real_flag = #{realFlag}
