mapper.select:
select
id,phone,password,nickname,user_type,user_rank,qq,avatar,is_frozen,register_time,register_ip,inviter_id,parent_id,remark,vip_expired_date,open_id,union_id,last_upgraded_time,is_root,root_name,is_boss,boss_name,boss_id,code,is_director,is_honor_director,is_shareholder,viewflag,lastlogin_time
from `usr_user`


		and user_rank = #{userRankEQ,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


		and is_shareholder = #{isShareholderEQ}


		 and nickname like #{nicknameLK}


		and parent_id is null


		and inviter_id = #{inviterIdEQ}


		and is_honor_director = #{isHonorDirectorEQ}


		and is_director = #{isDirectorEQ}



		and parent_id in

		#{item}



		and 1 = 2



		and boss_id = #{bossIdEQ}


		and is_frozen = #{isFrozenEQ}



		and id in

		#{item}



		and 1 = 2



		and user_type = #{userTypeEQ,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


		and register_time >= #{registerTimeGTE}


		and register_time < #{registerTimeLT}


		and parent_id = #{parentIdEQ}


		 and boss_name like #{bossNameLK}


		and is_boss = #{isBossEQ}


		and phone = #{phoneEQ}


		and is_root = #{isRootEQ}



		and inviter_id in

		#{item}



		and 1 = 2





		order by ${orderByAndDirection}


		order by id desc



	limit #{offset}, #{pageSize}

