mapper.update:
update `usr_user_upgrade`
user_id = #{userId}, from_user_rank = #{fromUserRank,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, to_user_rank = #{toUserRank,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, upgraded_time = #{upgradedTime},
where id = #{id}

update `usr_user_upgrade`
user_id = #{userUpgrade.userId}, from_user_rank = #{userUpgrade.fromUserRank,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler,javaType=com.zy.entity.usr.User$UserRank}, to_user_rank = #{userUpgrade.toUserRank,typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler,javaType=com.zy.entity.usr.User$UserRank}, upgraded_time = #{userUpgrade.upgradedTime},
where id = #{userUpgrade.id}

mapper.select:
select
id,user_id,from_user_rank,to_user_rank,upgraded_time
from `usr_user_upgrade`


		and user_id = #{userIdEQ}


		and upgraded_time < #{upgradedTimeLT}


		and upgraded_time >= #{upgradedTimeGTE}




		order by ${orderByAndDirection}


		order by id desc



	limit #{offset}, #{pageSize}

select count(*) from `usr_user_upgrade`


		and user_id = #{userIdEQ}


		and upgraded_time < #{upgradedTimeLT}


		and upgraded_time >= #{upgradedTimeGTE}
